INSERT INTO `book_m` VALUES (14,'နှမ်းခိုးစားသောမင်းသားငယ်',1500,1200,56,1,'စာအုပ်ဖော်ပြမှုမရှိပါ။',1,4,31,'7.3x0.10x10','Young_Prince.png',100,'bagan_khayeethae.pdf',0,'17/06/2022','myat kaung khant','17/06/2022','KaungKaung'),(15,'ကျောင်းတက်ချင်တဲ့ဂျားနီလေး',3000,NULL,56,NULL,'စာအုပ် ​ဖော်ပြမှု မရှိပါ။',1,4,22,'0x6x8','Jar_Ni.png',100,'book1.pdf',0,'17/06/2022','myat kaung khant',NULL,NULL),(16,'မင်းကုသနှင့်ပဘာဝတီပါဝေသောငါးရာ့ငါးဆယ် ရုပ်စုံဇာတ်လမ်းများ',2000,NULL,56,NULL,'စာအုပ် ​ဖော်ပြမှု မရှိပါ။',1,4,154,'7.4x0.3x10','Min_Khutha.png',100,'book2.pdf',0,'17/06/2022','myat kaung khant',NULL,NULL),(17,'ဘောလုံးကန်တဲ့စက်ရုပ်လေး',2000,NULL,56,NULL,'စာအုပ် ​ဖော်ပြမှု မရှိပါ။',1,3,22,'0x7.4x10','Robot.png',100,'book3.pdf',0,'17/06/2022','myat kaung khant',NULL,NULL),(18,'မြစိမ်းပြာကမာရွတ်',6000,NULL,57,NULL,'ရန်ကုန်မြို့ ကန်ထရိုက်တိုက်တလုံးပေါ်က မိသားစုတွေရဲ့ မတူညီတဲ့ဘဝကို\r\n ကွက်ကွက်ကွင်းကွင်းသရုပ်ဖော်ထားပြီး လူမျိုးစုံစရိုက်မျိုးစုံကို ရသစုံစုံမြင်တွေ့နိုင်တယ်။ \r\nလောကဓံကိုအန်တုရင်း ဘဝတွေကိုဘယ်လိုရုန်းကန်ကြသလဲ။ မပြေလည်မှုတွေကြားမှာ တစ်ဦးနဲ့တစ်ဦး\r\n ဖေးမပေးရင်း ပျော်ရွှင်အောင် နေနေကြတဲ့ မိသားစုတွေ၊ သဘာဝကျကျနဲ့ နေ့စဥ်ဘဝ​တွေကို \r\nဘယ်လိုဖြတ်သန်းနေတယ်ဆိုတာတွေ၊ လူငယ်နဲ့လူကြီးကြားက မတူညီတဲ့ စရိုက်တွေကိုလည်း နှိုင်းယှဥ်ပြထားတယ်။\r\n အဆုံးသတ်မှာတော့ လူတွေရဲ့ နေထိုင်မှုစရိုက်ကို ရှင်းရှင်းလင်းလင်းနဲ့ မငြီးငွေ့ရလေအောင်\r\n သရုပ်ဖော်ထားတာကြောင့် တကယ်ဖတ်လို့ကောင်းတဲ့ စာအုပ်ဖြစ်ပါတယ်။',2,1,382,'5.2x0.6x8.2','Mya_Sein_Pyar.png',100,'Mya_Sane.pdf',0,'17/06/2022','myat kaung khant',NULL,NULL),(19,'ပြုံး၍လည်းကန်တော့ခံတော်မူပါ ရယ်၍လည်းကန်တော့ခံတော်မူပါ',5000,NULL,57,NULL,'ကိုယ်ကိုယ်တိုင် တောင်ပြုံးရောက်သွားသလို သယ်ဆောင်သွားနိုင်တဲ့စာအုပ်။ \r\nပြီးတော့…. ကိုယ်ကိုယ်တိုင် နတ်ကတော် ဖြစ်သွားသလို ခံစားလာအောင် ရေဖွဲ့နိုင်တဲ့ စာအုပ်လေးပါ။',2,1,251,'5.2x0.4x8.2','Kantop.webp',99,'နုနုရည် အင်းဝ - ပြုံး၍လည်းကန်တော့ခံတော်မူပါ ရယ်၍လည်းကန်တော့ခံတော်မူပါ (1).pdf',0,'17/06/2022','myat kaung khant','17/06/2022','KaungKaung'),(20,'နှစ်ဆယ့်ရှစ်နှစ်ရှိပြီဖြစ်တဲ့ မိန်းမတစ်ယောက်',6000,NULL,57,NULL,'စာရေးသူမွေးဖွားစဥ်မှာ ကြုံတွေ့ရတဲ့ သူမ မိခင်ရဲ့ အခက်အခဲက အစချီလို့ အဘိုးအဘွားတွေရဲ့ အလိုလိုက်မှုကြောင့် စနေသမီးလေး ဆိုးသွမ်းပုံ၊\r\nအညာဒေသရဲ့ လူနေမှုဘဝ ခင်မင်ရတဲ့ သူငယ်ချင်းများ စာဖတ်ဝါသနာ ထက်သန်ပုံ အြေကာင်းပါတွဲလျက် ပုံဖော်ထားပါတယ်။\r\nကလေးဘဝအကြောင်းကို ရေးသားရာမှာလည်း ငယ်စဥ်အခါက ခံစားရတဲ့ တွေးခဲ့တဲ့ အတွေးလေးများအတိုင်းရေးထားတာမို့ သက်ဝင်လှပါတယ်။\r\nတက္ကသိုလ်ကျောင်းသူဘဝအကြောင်းကိုလည်း မန္တလေးတက္ကသိုလ်ရှုခင်းကို နောက်ခံထားလို့ အဆောင်နေကျောင်းသူဘဝရဲ့ သူငယ်ချင်းတွေနဲ့ ပျော်ရွှင်မှု အိမ်ကိုလွမ်းဆွတ်ရမှု တစ်ခါတစ်လေ ဝါသနာချင်းမတူလို့ အထီးကျန်မှုတွေကိုလည်း ရေးသားထားပါတယ်။',2,1,390,'5.2x0.6x8.2','28_years.png',100,'နုနုရည် အင်းဝ - နှစ်ဆယ့်ရှစ်နှစ်ရှိပြီဖြစ်တဲ့ မိန်းမတစ်ယောက်.pdf',0,'17/06/2022','myat kaung khant',NULL,NULL),(21,'နမ်းမှာလားမောင်ရဲ့ မွှေးတယ်ရှင်',5000,NULL,57,NULL,'သောင်းပြောင်းထွေလာ အောက်ခြေ လူတန်းစား ကျေးလက် လူမှု ဘ၀ရဲ့ စိတ်သဘောထား အသွားအလာလေးတွေ၊\r\n စကားပြောဟန်တွေ၊ ခေတ်စနစ် ရဲ့ အခြေအနေ တွေက လူတန်းစား တချို့ကို ဘယ်လိုထင်ဟပ်ပြီး ဒီလို လူတွေရဲ့\r\n ဘ၀ ကျင်လည်မှု ဖြစ်စဉ်တွေ ၊ရုန်းကန်မှု ပုံစံတွေ၊ ထွေလီပွေလီ နိစ္စဓူ၀ အဖြစ်အပျက်တွေထဲက မှတ်မှတ်ရရ \r\nအချိုးအကွေး တွေကို ရိုးရိုးရှင်းရှင်း စာဖတ်သွက်သွက် အတွေးကြာကြာ နဲ့ ဖတ်ရမယ့် ၀ တ္ထုတိုလေးတွေပါ။ \r\nပြန်လည်စုစည်းထားတဲ့ စာစဉ် ၀ တ္ထုတိုလေးတွေမို့ မဖတ်ဖူးသူ အတွက်တော့ အကုန်နီးပါး အသစ်တွေချည်းပါပဲ။\r\nဖတ်ဖူးသူ အနေနဲ့ကျတော့ တစုတစည်းတည်း စုစည်းထားတဲ့ အတွေးခံစားမှုရသ စာစု ဖြစ်တာမျိုး တစိမ့်စိမ့်နဲ့ \r\nပြုံးရင်း၊ တွေးရင်း၊ ‌ငေးရင်း နဲ့ နွေးထွေးစေမယ့် စာအုပ်လေးပါ။ ',2,1,305,'5.4x0.5x8.3','Nan_mhar.png',100,'နုနုရည် အင်းဝ - နမ်းမှာလားမောင်ရဲ့ မွှေးတယ်ရှင်နှင့် အခြားဝတ္ထုတိုများ.pdf',0,'17/06/2022','myat kaung khant',NULL,NULL),(22,'ကိုရီးယား အခြေခံသဒ္ဒါနှင့် လေ့ကျင့်ခန်းများ',3500,NULL,56,NULL,'စာအုပ် ​ဖော်ပြမှု မရှိပါ။',6,5,174,'5.8x0.4x8.2','korea_basic.png',100,'book1.pdf',0,'17/06/2022','myat kaung khant',NULL,NULL),(23,'လွယ်ကူလေ့လာ ဂျပန်သဒ္ဒါနှင့် စကား',3500,NULL,56,NULL,'စာအုပ် ​ဖော်ပြမှု မရှိပါ။',6,5,182,'5.6x0.4x8.3','lawe_kuJp.png',100,'book2.pdf',0,'17/06/2022','myat kaung khant',NULL,NULL),(24,'CSK အခြေခံတရုတ်စကား